import './App.css';
import { Icon, Input, Button, Image, Message, Card  } from 'semantic-ui-react'
import React, {Component} from 'react';

var username = "";

class App extends Component{
    constructor() 
    {
      super();
      this.state = {
        showDetails : false,
        login : "",
        avatar_url : "",
        html_url : "",
        followers_url : "",
        following_url : "",
        subscriptions_url : "",
        repos_url : "",
        name : "",
        company : "",
        blog : "",
        location : "",
        email : "",
        bio : "",
        twitter_username : "",
        public_repos : 0,
        followers : 0,
        following : 0, 
        created_at : "",
      }
    }

    fetchUrl = async () => {
        var url = "https://api.github.com/users/"+username;
        await fetch(url)
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                showDetails: true,
                login : result['login'],
                avatar_url : result['avatar_url'],
                html_url : result['html_url'],
                followers_url : ['followers_url'],
                following_url : result['following_url'],
                subscriptions_url : result['subscriptions_url'],
                repos_url : result['repos_url'],
                name : result['name'],
                company : result['company'],
                blog : result['blog'],
                location : result['location'],
                email : result['email'],
                bio : result['bio'],
                twitter_username : result['twitter_username'],
                public_repos : result['public_repos'],
                followers : result['followers'],
                following : result['following'],  
                created_at : result['created_at'],
              });
              console.log("FETCH SUCCESSFULL");
            },
            (error) => {
              console.log("FETCH FAILED : ",error);
            }
          )
      }

    handleMessage = (e) => {
        username = e.target.value;
    }

    render(){
        var detailsSection= "";
        var scroll = this.state.showDetails ? <Message floating size='small'>Please Scroll Down !</Message> : ""
        detailsSection = this.state.showDetails ? (
            <Card>
              <Image src={this.state.avatar_url} wrapped ui={false} />
              <Card.Content>
                <Card.Header>{this.state.name}</Card.Header>
                <Card.Meta>Joined in {this.state.created_at}</Card.Meta>
                <Card.Description>
                {this.state.name} works in {this.state.company} living in {this.state.location}.
                </Card.Description>
              </Card.Content>
              <Card.Content extra>
                <a>
                  <Icon name='user' />
                  10 Friends
                </a>
              </Card.Content>
            </Card>
          ) : "";
        return ( 
            <div className = "App" >
                <header className = "App-header" >
                    <a className = "App-name" href = "#">
                        Gitify 
                    </a>
                    <br/> <br/>
                    <Icon disabled name = 'github' size = 'massive'/> 
                    < br/>
                    <p>
                        <Input className = "App-input" icon = { < Icon name = 'search' inverted circular link/>} onChange = { this.handleMessage } placeholder = 'Github Username' size = 'mini' />
                        <Button color = 'green' size = 'massive' onClick={ this.fetchUrl }> Gitify </Button> 
                        {scroll}
                    </p>             
                    <div className="App-detailsSection">
                        {detailsSection}
                    </div>       
                </header> 
            </div>
        );
    }
}

export default App;
