import React, {Component} from 'react'
import { Grid, Image } from 'semantic-ui-react'
import './PublicRepo.css'

export default class PublicRepo extends Component{
    constructor() 
    {
      super();
      this.state = {
        repos : []

      }
    }

    fetchUrl = async () => {
        var url = "https://api.github.com/users/"+username;
        await fetch(url)
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                showDetails: true,
                login : result['login'],
                avatar_url : result['avatar_url'],
                html_url : result['html_url'],
                followers_url : ['followers_url'],
                following_url : result['following_url'],
                subscriptions_url : result['subscriptions_url'],
                repos_url : result['repos_url'],
                name : result['name'],
                company : result['company'],
                blog : result['blog'],
                location : result['location'],
                email : result['email'],
                bio : result['bio'],
                twitter_username : result['twitter_username'],
                public_repos : result['public_repos'],
                followers : result['followers'],
                following : result['following'],  
                created_at : result['created_at'],
              });
              console.log("FETCH SUCCESSFULL");
            },
            (error) => {
              console.log("FETCH FAILED : ",error);
            }
          )
      }

    render(){
        return(
            <div>

            </div>
        );
    }
}
