import './App.css';
import { Icon, Input, Button } from 'semantic-ui-react'
import Fetch from './utils/fetch'
import React, {Component} from 'react';

export var username = "";
var detailsSection= "";
var showDetailsHere = false;

class App extends Component{
    handleMessage = (e) => {
        username = e.target.value;
    }

    onSubmit = () =>{
        console.log("Button Clicked");
        showDetailsHere = true;
        console.log(<Fetch />);
        detailsSection = showDetailsHere ? <Fetch/> : "";
        console.log(detailsSection);
    }

    render(){
        return ( 
            <div className = "App" >
                <header className = "App-header" >
                    <a className = "App-name" href = "#">
                        Gitify 
                    </a>
                    <br/> <br/>
                    <Icon disabled name = 'github' size = 'massive'/> 
                    < br/>
                    <p>
                        <Input className = "App-input" icon = { < Icon name = 'search' inverted circular link/>} onChange = { this.handleMessage } placeholder = 'Github Username' size = 'mini' />
                        <Button color = 'green' size = 'massive' onClick={this.onSubmit}> Gitify </Button> 
                        {detailsSection}
                    </p>
                </header > 
            </div>
        );
    }
}

export default App;
