import React, {Component} from 'react'
import './PublicRepo.css'

export default class PublicRepo extends Component{
    constructor(props) 
    {
      super(props);
      this.state = {
        repos : []
      }
    }

    componentDidMount(){
      this.fetchPublicApiUrl();
    }

    fetchPublicApiUrl = async () => {
        var url = "https://api.github.com/users/"+this.props.username+"/repos";
        await fetch(url)
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                repos : result
              });
              console.log("PUBLIC REPO FETCH SUCCESSFULL");
            },
            (error) => {
              console.log("PUBLIC REPO FETCH FAILED : ",error);
            }
          )
      }

      extractDate(date){
        return date.substring(0,10);
      }

      extractDescription(description){
        // console.log("extractDescription called ");
        return description ? (<p className="publicRepoDescription">Description : <span className="publicRepoDescriptionContent"> {description} </span></p>) : "";
      }

      rowData = (fork, html_url, name, created_at, size, default_branch, description, id) => {
        console.log("ROW DATA : ", name, " FORK : ", fork);
        var dataRow = "none";
        if(fork)
        {
          dataRow = (
                  <div className="row">
                    <p className="repoName">
                      <a href={html_url}>{name}</a>
                    </p>
                    <p className="createdAt">
                      Created on <span className="publicRepoDate"> {this.extractDate(created_at)}</span> <span className="divider"> | </span> Size : <span className="publicRepoSize">{size} KB</span> <span className="divider"> | </span> Default Branch : <span className="publicRepoDefaultBranch"> {default_branch} </span>
                    </p>
                      {this.extractDescription(description)}
                  </div>
                );
        }
        console.log("DATA ROW : ", dataRow);
        return dataRow ;
      }

    render(){
      // const rowData = (fork, html_url, name, created_at, size, default_branch, description, id) => {
      //   console.log("ROW DATA : ", name, " FORK : ", fork);
      //   var data = "";
      //   if(fork)
      //   {
      //     data = (
      //             <div key={id} className="row">
      //               <p className="repoName">
      //                 <a href={html_url}>{name}</a>
      //               </p>
      //               <p className="createdAt">
      //                 Created on <span className="publicRepoDate"> {this.extractDate(created_at)}</span> <span className="divider"> | </span> Size : <span className="publicRepoSize">{size} KB</span> <span className="divider"> | </span> Default Branch : <span className="publicRepoDefaultBranch"> {default_branch} </span>
      //               </p>
      //                 {this.extractDescription(description)}
      //             </div>
      //           );
      //   }
      //   console.log("DATA : ", data);
      //   return data ;
      // }

        return(
            <div>
              <div className="publicRepo">Public Repositories</div>
              {this.state.repos.map(home => 
                {this.rowData(home.fork, home.html_url, home.name, home.created_at, home.size, home.default_branch, home.description, home.id)}
                // (
                //   <div key={home.id} className="row">
                //     <p className="repoName">
                //       <a href={home.html_url}>{home.name}</a>
                //     </p>
                //     <p className="createdAt">
                //       Created on <span className="publicRepoDate"> {this.extractDate(home.created_at)}</span> <span className="divider"> | </span> Size : <span className="publicRepoSize">{home.size} KB</span> <span className="divider"> | </span> Default Branch : <span className="publicRepoDefaultBranch"> {home.default_branch} </span>
                //     </p>
                //       {this.extractDescription(home.description)}
                //   </div>
                // )
              )}
            </div>
        );
    }
}
