import './App.css';
import { Icon, Input, Button, Image } from 'semantic-ui-react'
// import Fetch from './utils/fetch'
import React, {Component} from 'react';

export var username = "";
var detailsSection= "";

class App extends Component{
    constructor() 
    {
      super();
      this.state = {
        showDetails : false,
        login : "",
        avatar_url : "",
        html_url : "",
        followers_url : "",
        following_url : "",
        subscriptions_url : "",
        repos_url : "",
        name : "",
        company : "",
        blog : "",
        location : "",
        email : "",
        bio : "",
        twitter_username : "",
        public_repos : 0,
        followers : 0,
        following : 0, 
      }
    }

    handleClick = () =>{
        this.setState({showDetails : true});
    }

    fetchUrl = () => {
        // console.log("USERNAME : ", username);
        var url = "https://api.github.com/users/"+username;
        // console.log("URL : ",url)
        fetch(url)
          .then(res => res.json())
          .then(
            (result) => {
              console.log("RESULT : ",result);
              this.setState({
                showDetails: true,
                login : result['login'],
                avatar_url : result['avatar_url'],
                html_url : result['html_url'],
                followers_url : ['followers_url'],
                following_url : result['following_url'],
                subscriptions_url : result['subscriptions_url'],
                repos_url : result['repos_url'],
                name : result['name'],
                company : result['company'],
                blog : result['blog'],
                location : result['location'],
                email : result['email'],
                bio : result['bio'],
                twitter_username : result['twitter_username'],
                public_repos : result['public_repos'],
                followers : result['followers'],
                following : result['following'],  
              });
            },
            (error) => {
              console.log(error);
            }
          )
      }

    handleMessage = (e) => {
        username = e.target.value;
    }

    onSubmit = () =>{
        console.log("Button Clicked");
        console.log("SHOW DETAILS : ",this.state.showDetails);
        console.log("STATE B/F : ", this.state);
        this.handleClick();
        this.fetchUrl();
        console.log("STATE A/F : ", this.state);
        console.log("DETAILS SECTION : ",detailsSection);
        console.log("SHOW DETAILS : ",this.state.showDetails);
        detailsSection = this.state.showDetails ? <Image src={this.state.avatar_url} size='medium' circular /> : "";
        console.log("DETAILS SECTION : ",detailsSection);
    }

    render(){
        return ( 
            <div className = "App" >
                <header className = "App-header" >
                    <a className = "App-name" href = "#">
                        Gitify 
                    </a>
                    <br/> <br/>
                    <Icon disabled name = 'github' size = 'massive'/> 
                    < br/>
                    <p>
                        <Input className = "App-input" icon = { < Icon name = 'search' inverted circular link/>} onChange = { this.handleMessage } placeholder = 'Github Username' size = 'mini' />
                        <Button color = 'green' size = 'massive' onClick={ this.onSubmit }> Gitify </Button> 
                        {detailsSection}
                    </p>
                </header > 
            </div>
        );
    }
}

export default App;
