import { username } from '../App';
import {AvatarCircular} from '../components/avatar';

var showDetails = false;

export const fetchUrl = () => {
    fetch("https://api.github.com/users/"+username)
      .then(res => res.json())
      .then(
        (result) => {
          showDetails = true;
          console.log(result);
        },
        (error) => {
          console.log(error);
        }
      )
}
